/*==================================================
/src/store/reducers/campuses.js

This is a Reducer function that accepts 2 parameters: the previous
state object and an action object. Depending on the Action object,
the Reducer updates the State and returns the new State object.
It also defines the State and its default initial value.
================================================== */
import * as at from "../actions/actionTypes";  // Import Action Types

// REDUCER:
const allCampuses = (state = [], action) => {  
  switch (action.type) {

    // Get all campuses
    case at.FETCH_ALL_CAMPUSES:
      return action.payload;

    // Add a new campus
    case at.ADD_CAMPUS:
      return [...state, action.payload];

      case at.EDIT_CAMPUS:
        return state.map((campus) =>
          campus.id === action.payload.id ? action.payload : campus
        );


    default:
      // If the Reducer doesn't recognize the Action Type,
      // return the previous State unchanged.
      return state;
  }
};

export default allCampuses;
