/*==================================================
NewCampusView.js
==================================================*/

import React from "react";

const NewCampusView = (props) => {
  const { handleChange, handleSubmit, name, address, description, imageUrl } = props;

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Add a New Campus</h1>

      <form onSubmit={handleSubmit}>

        <div>
          <label>Name: </label>
          <input
            type="text"
            name="name"
            value={name}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Address: </label>
          <input
            type="text"
            name="address"
            value={address}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Description: </label>
          <input
            type="text"
            name="description"
            value={description}
            onChange={handleChange}
          />
        </div>

        <div>
          <label>Image URL: </label>
          <input
            type="text"
            name="imageUrl"
            value={imageUrl}
            onChange={handleChange}
            placeholder="https://example.com/campus.jpg"
          />
        </div>

        <button type="submit" style={{ marginTop: "10px" }}>
          Add Campus
        </button>

      </form>
    </div>
  );
};

export default NewCampusView;
