/*==================================================
  CampusView.js
  This View component is responsible for displaying:

  - A single campus (name, address, description)
  - The campus image
  - A list of students enrolled in the campus
  - A button to edit the campus

  It receives its data from CampusContainer.
==================================================*/

import { Link } from "react-router-dom";

const CampusView = (props) => {
  const { campus } = props;

  // Prevent rendering before campus data loads
  if (!campus || !campus.id) {
    return <h2>Loading campus...</h2>;
  }

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>

      {/* Campus Name */}
      <h1>{campus.name}</h1>

      {/* Campus Image */}
      {campus.imageUrl && (
        <img
          src={campus.imageUrl}
          alt={campus.name}
          style={{
            width: "260px",
            borderRadius: "10px",
            margin: "15px 0",
          }}
        />
      )}

      {/* Campus Address + Description */}
      <p>{campus.address}</p>
      <p>{campus.description}</p>

      {/* Edit Button */}
      <Link to={`/campus/${campus.id}/edit`}>
        <button style={{ marginTop: "10px" }}>Edit Campus</button>
      </Link>

      <hr style={{ margin: "30px 0" }} />

      {/* Students Section */}
      <h2>Students Enrolled</h2>

      {/* If no students */}
      {(!campus.students || campus.students.length === 0) && (
        <p>No students currently enrolled.</p>
      )}

      {/* List of students */}
      {campus.students &&
        campus.students.map((student) => {
          const fullName = `${student.firstname} ${student.lastname}`;
          return (
            <div key={student.id} style={{ marginBottom: "12px" }}>
              <Link to={`/student/${student.id}`}>
                <h3 style={{ color: "#4B0082" }}>{fullName}</h3>
              </Link>
            </div>
          );
        })}
    </div>
  );
};

export default CampusView;
