/*==================================================
CampusView.js
Displays a campus + its students + image + enroll form
==================================================*/

import { Link } from "react-router-dom";

const CampusView = ({ campus, allStudents, handleEnroll }) => {

  if (!campus || !campus.id) {
    return <h2>Loading campus...</h2>;
  }

  // Students who are not enrolled anywhere OR enrolled in another campus
  const unenrolledStudents = allStudents.filter(
    (s) => s.campusId !== campus.id
  );

  return (
    <div style={{ textAlign: "center" }}>
      <h1>{campus.name}</h1>

      {/* CAMPUS IMAGE */}
      {campus.imageUrl && (
        <img
          src={campus.imageUrl}
          alt={`${campus.name} campus`}
          style={{
            width: "250px",
            borderRadius: "6px",
            marginBottom: "15px",
            border: "1px solid #ccc"
          }}
        />
      )}

      <p><strong>{campus.address}</strong></p>
      <p>{campus.description}</p>

      <Link to={`/campus/${campus.id}/edit`}>
        <button>Edit Campus</button>
      </Link>

      <hr style={{ margin: "25px 0" }} />

      {/* ====================== */}
      {/* ENROLL STUDENT SECTION */}
      {/* ====================== */}
      <h2>Enroll a Student in This Campus</h2>

      {unenrolledStudents.length > 0 ? (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            const studentId = e.target.studentId.value;
            handleEnroll(studentId, campus.id);
          }}
        >
          <select name="studentId" style={{ padding: "6px", marginRight: "10px" }}>
            {unenrolledStudents.map((student) => (
              <option key={student.id} value={student.id}>
                {student.firstname} {student.lastname}
              </option>
            ))}
          </select>

          <button type="submit">Enroll Student</button>
        </form>
      ) : (
        <p>No available students to enroll.</p>
      )}

      <hr style={{ margin: "25px 0" }} />

      {/* ====================== */}
      {/* STUDENTS ENROLLED */}
      {/* ====================== */}
      <h2>Students Enrolled</h2>

      {campus.students && campus.students.length > 0 ? (
        campus.students.map((student) => (
          <div key={student.id} style={{ marginBottom: "25px" }}>

            {/* Student Image */}
            {student.imageUrl && (
              <img
                src={student.imageUrl}
                alt="student"
                style={{
                  width: "120px",
                  borderRadius: "6px",
                  marginBottom: "10px"
                }}
              />
            )}

            {/* Name */}
            <Link to={`/student/${student.id}`}>
              <h3>{student.firstname} {student.lastname}</h3>
            </Link>

            {/* Email */}
            <p><strong>Email:</strong> {student.email}</p>

            {/* GPA */}
            <p><strong>GPA:</strong> {student.gpa ? student.gpa : "N/A"}</p>

            <hr style={{ width: "60%", margin: "20px auto" }} />
          </div>
        ))
      ) : (
        <p>No students enrolled at this campus.</p>
      )}

    </div>
  );
};

export default CampusView;
