/*==================================================
AllCampusesView.js
Displays all campuses, including campus images.
================================================== */

import PropTypes from "prop-types";
import { Link } from "react-router-dom";

const AllCampusesView = ({ allCampuses, deleteCampus }) => {

  // If no campuses exist
  if (!allCampuses || allCampuses.length === 0) {
    return (
      <div>
        <p>There are no campuses.</p>
        <Link to="/newcampus">
          <button>Add New Campus</button>
        </Link>
      </div>
    );
  }

  return (
    <div>
      <h1>All Campuses</h1>

      {allCampuses.map((campus) => (
        <div key={campus.id} style={{ marginBottom: "35px" }}>

          {/* Campus Name links to single-campus view */}
          <Link to={`/campus/${campus.id}`}>
            <h2>{campus.name}</h2>
          </Link>

          {/* Campus Image (safe check) */}
          {campus.imageUrl && (
            <img
              src={campus.imageUrl}
              alt={`${campus.name} logo`}
              style={{
                width: "180px",
                height: "auto",
                borderRadius: "6px",
                marginBottom: "10px",
                border: "1px solid #ccc",
              }}
            />
          )}

          <p><strong>Campus ID:</strong> {campus.id}</p>
          <p>{campus.address}</p>
          <p>{campus.description}</p>

          {/* DELETE BUTTON */}
          <button
            onClick={() => deleteCampus(campus.id)}
            style={{
              backgroundColor: "red",
              color: "white",
              padding: "6px 12px",
              borderRadius: "4px",
              cursor: "pointer",
              marginTop: "10px",
            }}
          >
            Delete Campus
          </button>

          <hr />
        </div>
      ))}

      {/* Add new campus */}
      <Link to="/newcampus">
        <button>Add New Campus</button>
      </Link>
    </div>
  );
};

AllCampusesView.propTypes = {
  allCampuses: PropTypes.array.isRequired,
  deleteCampus: PropTypes.func.isRequired,
};

export default AllCampusesView;
